import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // To handle redirects

const LoginPage = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        setIsLoading(true);
        setError('');  // Reset error state

        try {
            const response = await axios.post('http://localhost:8081/api/login', { username, password });
            alert(response.data.message); // Show login success message
            // Optionally, store user data in localStorage or state
            localStorage.setItem('user', JSON.stringify(response.data.user)); // Example storage
            navigate('/landingPage'); // Redirect to home page or any protected route
        } catch (err) {
            setError(err.response?.data?.message || 'Error logging in');
        } finally {
            setIsLoading(false); // Reset loading state
        }
    };

    return (
        <div className="login-container">
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Username:</label>
                    <input 
                        type="text" 
                        value={username} 
                        onChange={(e) => setUsername(e.target.value)} 
                    />
                </div>
                <div>
                    <label>Password:</label>
                    <input 
                        type="password" 
                        value={password} 
                        onChange={(e) => setPassword(e.target.value)} 
                    />
                </div>
                <button type="submit" disabled={isLoading}>
                    {isLoading ? 'Logging in...' : 'Login'}
                </button>
            </form>
            {error && <div>{error}</div>}
        </div>
    );
};

export default LoginPage;
