import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const LoginPage = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate(); // Initialize the navigate hook

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('http://localhost:8081/api/users/login', { username, password });
            alert(response.data.message); // Display success message

            // Assuming the response contains user data, store it in localStorage
            const userData = {
                username: response.data.username,
                email: response.data.email
            };
            localStorage.setItem('userData', JSON.stringify(userData)); // Store user data as a JSON string

            navigate('/landingPage'); // Redirect to the landing page
        } catch (err) {
            console.error('Login error:', err);
            setError(err.response?.data?.message || 'Error logging in');
        }
    };




    return (
        <div className="login-container">
            <form onSubmit={handleSubmit}>
                <div>
                    <label>Username:</label>
                    <input 
                        type="text" 
                        value={username} 
                        onChange={(e) => setUsername(e.target.value)} 
                    />
                </div>
                <div>
                    <label>Password:</label>
                    <input 
                        type="password" 
                        value={password} 
                        onChange={(e) => setPassword(e.target.value)} 
                    />
                </div>
                <button type="submit">Login</button>
            </form>

            {/* Sign Up button with the same styling */}
            <button 
                onClick={() => navigate('/signup')} // Redirects to the sign-up page
                style={{ marginTop: '10px', backgroundColor: '#007bff', color: 'white', padding: '10px', border: 'none', cursor: 'pointer' }}
            >
                Sign Up
            </button>

            {error && <div>{error}</div>}
        </div>
    );
};

export default LoginPage;
