import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import backendURL from '../apiConfig';

export default function EditLocationPage() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [locationData, setLocationData] = useState({
    locationName: '',
    locationType: '',
    address: '',
    description: '',
  });
  const [error, setError] = useState('');

  useEffect(() => {
    axios.get(`${backendURL}/api/locations/${id}`)
      .then(res => {
        setLocationData({
          locationName: res.data.locationName,
          locationType: res.data.locationType,
          address: res.data.address,
          description: res.data.description,
        });
      })
      .catch(err => {
        console.error('Error fetching location details:', err);
        setError('Failed to load location details.');
      });
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setLocationData(prev => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await axios.put(`${backendURL}/api/locations/${id}`, locationData);
      navigate(`/locations/${id}`);
    } catch (err) {
      console.error('Error updating location:', err);
      setError('Failed to update location.');
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-sage font-inknut text-brunswick px-4 py-10">
      <h1 className="text-3xl font-bold mb-6">Edit Location</h1>

      {error && (
        <div className="text-imperial mb-4">
          {error}
        </div>
      )}

      <form onSubmit={handleSubmit} className="w-full max-w-2xl bg-alabaster p-8 rounded-lg shadow-lg space-y-4">
        <div>
          <label className="block mb-1 font-semibold">Location Name</label>
          <input
            type="text"
            name="locationName"
            value={locationData.locationName}
            onChange={handleChange}
            className="w-full px-4 py-2 rounded border border-gray-300 focus:ring-2 focus:ring-fern focus:outline-none"
            required
          />
        </div>

        <div>
          <label className="block mb-1 font-semibold">Location Type</label>
          <input
            type="text"
            name="locationType"
            value={locationData.locationType}
            onChange={handleChange}
            className="w-full px-4 py-2 rounded border border-gray-300 focus:ring-2 focus:ring-fern focus:outline-none"
            required
          />
        </div>

        <div>
          <label className="block mb-1 font-semibold">Address</label>
          <input
            type="text"
            name="address"
            value={locationData.address}
            onChange={handleChange}
            className="w-full px-4 py-2 rounded border border-gray-300 focus:ring-2 focus:ring-fern focus:outline-none"
            required
          />
        </div>

        <div>
          <label className="block mb-1 font-semibold">Description</label>
          <textarea
            name="description"
            value={locationData.description}
            onChange={handleChange}
            rows="4"
            className="w-full px-4 py-2 rounded border border-gray-300 focus:ring-2 focus:ring-fern focus:outline-none resize-none"
            required
          />
        </div>

        <button
          type="submit"
          className="w-full bg-fern hover:bg-hunter text-alabaster font-semibold py-3 rounded transition"
        >
          Save Changes
        </button>
      </form>
    </div>
  );
}
