import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import backendURL from '../apiConfig';

export default function ProfilePage() {
  const [user, setUser] = useState(null);
  const [locations, setLocations] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      try {
        const parsedUser = JSON.parse(storedUser);
        setUser(parsedUser);

        // Fetch locations by this user
        axios.get(`${backendURL}/api/locations`)
          .then((res) => {
            const userLocations = res.data.filter(location => location.username === parsedUser.username);
            setLocations(userLocations);
          })
          .catch((err) => {
            console.error('Error fetching locations:', err);
            setError('Failed to load locations.');
          });
      } catch {
        setUser(null);
      }
    }
  }, []);

  const renderStars = (rating) => {
    const stars = [];
    const roundedRating = Math.round(rating || 0);
    for (let i = 0; i < 5; i++) {
      if (i < roundedRating) {
        stars.push(<span key={i} className="text-hunter text-lg">★</span>);
      } else {
        stars.push(<span key={i} className="text-hunter text-lg">☆</span>);
      }
    }
    return stars;
  };

  return (
    <div className="min-h-screen pt-20 px-4 flex flex-col items-center bg-inherit font-inknut text-brunswick">
      <h1 className="text-4xl font-bold mb-8">My Locations</h1>

      {error && <p className="text-imperial mb-6">{error}</p>}

      {locations.length > 0 ? (
        <div className="grid grid-cols-1 gap-6 w-full max-w-2xl">
          {locations.map((location) => (
            <div key={location._id} className="p-6 border rounded-lg bg-alabaster shadow-lg">
              <div className="flex justify-between items-center">
                <Link to={`/locations/${location._id}`} className="text-2xl font-bold text-fern hover:text-hunter">
                  {location.locationName}
                </Link>
                {/* Display average stars if available */}
                {location.averageRating ? (
                  <div className="flex">{renderStars(location.averageRating)}</div>
                ) : (
                  <p className="italic text-sm text-gray-500 ml-2">Not yet rated</p>
                )}
              </div>

              <p className="text-sm text-gray-600">Created by: {location.username}</p>
              <p className="text-sm"><strong>Type:</strong> {location.locationType}</p>
              <p className="text-sm"><strong>Address:</strong> {location.address}</p>
              <p className="text-sm mt-2">{location.description}</p>
            </div>
          ))}
        </div>
      ) : (
        <p>No locations created yet.</p>
      )}
    </div>
  );
}
