import React, { useState, useEffect } from 'react';

export default function LandingPage() {
    const [user, setUser] = useState(null);
    const [error, setError] = useState(null);

    useEffect(() => {
        try {
            const storedUser = localStorage.getItem('user'); // Retrieve the user data from localStorage

            if (storedUser) {
                const userData = JSON.parse(storedUser); // Parse the stored data
                setUser(userData); // Set the user data in state
            } else {
                setError('No user data found');
            }
        } catch (err) {
            setError('Error parsing user data');
        }
    }, []);

    return (
        <div className="flex flex-col items-center justify-center h-screen bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-white">
            <h1 className="text-4xl font-bold mb-6">
                Welcome to Demeter, {user ? user.username : 'Loading...'}
            </h1>
            <p>Email: {user ? user.email : 'Loading...'}</p>
            <button
                className="px-6 py-3 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition"
                onClick={() => {
                    localStorage.removeItem('user'); // Remove user from localStorage
                    window.location.reload(); // Reload page to clear state
                }}
            >
                Log Out
            </button>
            {error && <p>{error}</p>} {/* Display error if any */}
        </div>
    );
}
