import React, { useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';

export default function CreateLocationPage() {
  const { state } = useLocation(); // Get the passed state from the LandingPage
  const [locationName, setLocationName] = useState('');
  const [locationType, setLocationType] = useState('');
  const [address, setAddress] = useState('');
  const [username] = useState(state?.username || ''); // Get username from state or set empty if not found
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/locations/createLocation', locationData);
      console.log('Location created:', response.data);
    } catch (error) {
      console.error('Error creating location:', error);
    }
  };


  return (
    <div className="flex flex-col items-center justify-center h-screen bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-white">
      <h1 className="text-4xl font-bold mb-6">Create a New Location</h1>
      <form onSubmit={handleSubmit} className="w-full max-w-md">
        <div className="mb-4">
          <label htmlFor="locationName" className="block text-lg font-semibold">
            Location Name:
          </label>
          <input
            type="text"
            id="locationName"
            value={locationName}
            onChange={(e) => setLocationName(e.target.value)}
            className="w-full p-2 border border-gray-300 rounded-md"
            required
          />
        </div>
        <div className="mb-4">
          <label htmlFor="locationType" className="block text-lg font-semibold">
            Location Type:
          </label>
          <input
            type="text"
            id="locationType"
            value={locationType}
            onChange={(e) => setLocationType(e.target.value)}
            className="w-full p-2 border border-gray-300 rounded-md"
            required
          />
        </div>
        <div className="mb-4">
          <label htmlFor="address" className="block text-lg font-semibold">
            Address:
          </label>
          <input
            type="text"
            id="address"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            className="w-full p-2 border border-gray-300 rounded-md"
            required
          />
        </div>
        {/* Username field will be hidden and auto-filled */}
        <input type="hidden" value={username} />
        <button
          type="submit"
          className="px-6 py-3 bg-green-500 text-white rounded-lg hover:bg-green-600 transition"
        >
          Create Location
        </button>
      </form>
    </div>
  );
}
