import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function LoginPage() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('http://localhost:8081/api/users/login', { username, password });

            // Store user data in localStorage
            const userData = {
                username: response.data.username,
                email: response.data.email,
            };
            localStorage.setItem('userData', JSON.stringify(userData));

            // Redirect to the landing page after login
            navigate('/landing');
        } catch (err) {
            setError('Invalid username or password');
        }
    };

    return (
        <div className="flex flex-col items-center justify-center h-screen bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-white">
            <h1 className="text-4xl font-bold mb-6">Login</h1>
            <form onSubmit={handleSubmit} className="w-1/3">
                <div className="mb-4">
                    <label className="block">Username</label>
                    <input 
                        type="text" 
                        value={username} 
                        onChange={(e) => setUsername(e.target.value)} 
                        className="w-full p-2 mt-2 border border-gray-300 rounded"
                    />
                </div>
                <div className="mb-4">
                    <label className="block">Password</label>
                    <input 
                        type="password" 
                        value={password} 
                        onChange={(e) => setPassword(e.target.value)} 
                        className="w-full p-2 mt-2 border border-gray-300 rounded"
                    />
                </div>
                <button type="submit" className="w-full py-2 bg-blue-500 text-white rounded">
                    Log In
                </button>
            </form>
            {error && <div className="mt-4 text-red-500">{error}</div>}
        </div>
    );
}
