import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const LoginPage = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post('http://localhost:8081/api/users/login', { username, password });

            console.log(response.data); // Check what the response data looks like
            if (response.data) {
                // Assuming response.data contains the user info
                const { username, email } = response.data;
                localStorage.setItem('user', JSON.stringify({ username, email }));

                navigate('/landingPage'); // Navigate to the landing page
            } else {
                setError('Invalid response data');
            }
        } catch (err) {
            setError('Login failed. Please check your credentials.');
        }
    };


    return (
        <div>
            <form onSubmit={handleSubmit}>
                <label>Username:</label>
                <input 
                    type="text" 
                    value={username} 
                    onChange={(e) => setUsername(e.target.value)} 
                />
                <label>Password:</label>
                <input 
                    type="password" 
                    value={password} 
                    onChange={(e) => setPassword(e.target.value)} 
                />
                <button type="submit">Login</button>
            </form>

            {error && <div>{error}</div>}
        </div>
    );
};

export default LoginPage;
