import backendURL from '../apiConfig';
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

export default function AllLocationsPage() {
  const [locations, setLocations] = useState([]);
  const [sortBy, setSortBy] = useState('date'); // 'date' or 'alphabetical'

  useEffect(() => {
    axios.get(`${backendURL}/api/locations`)
      .then((res) => setLocations(res.data))
      .catch((err) => console.error('Error fetching locations:', err));
  }, []);

  const sortedLocations = [...locations].sort((a, b) => {
    if (sortBy === 'alphabetical') {
      return a.locationName.localeCompare(b.locationName);
    } else {
      return new Date(b.createdAt) - new Date(a.createdAt); // Newest first
    }
  });

  const renderStars = (rating) => {
    const stars = [];
    const rounded = Math.round(rating);
    for (let i = 0; i < 5; i++) {
      stars.push(
        <span key={i} className="text-hunter text-xl">
          {i < rounded ? '★' : '☆'}
        </span>
      );
    }
    return stars;
  };

  return (
    <div className="min-h-screen px-4 py-10 bg-inherit font-inknut text-brunswick">
      <div className="flex flex-col md:flex-row md:justify-between items-center mb-8">
        <h1 className="text-4xl font-bold mb-4 md:mb-0">All Locations</h1>

        {/* Filter Controls */}
        <div className="flex space-x-4">
          <button
            onClick={() => setSortBy('date')}
            className={`px-4 py-2 rounded font-semibold transition ${sortBy === 'date' ? 'bg-fern text-alabaster' : 'bg-alabaster text-brunswick hover:bg-fern hover:text-alabaster'}`}
          >
            Newest
          </button>
          <button
            onClick={() => setSortBy('alphabetical')}
            className={`px-4 py-2 rounded font-semibold transition ${sortBy === 'alphabetical' ? 'bg-fern text-alabaster' : 'bg-alabaster text-brunswick hover:bg-fern hover:text-alabaster'}`}
          >
            A-Z
          </button>
        </div>
      </div>

      {/* Locations List (vertical like search results) */}
      {sortedLocations.length > 0 ? (
        <div className="grid grid-cols-1 gap-6 max-w-2xl mx-auto">
          {sortedLocations.map((location) => (
            <div key={location._id} className="p-6 bg-alabaster rounded-lg shadow hover:scale-105 transition">
              <Link to={`/locations/${location._id}`}>
                <h2 className="text-2xl font-bold text-fern hover:text-hunter mb-2">
                  {location.locationName}
                </h2>

                {/* Average rating OR Not yet rated */}
                <div className="flex justify-start items-center mb-4">
                  {location.averageRating ? renderStars(location.averageRating) : (
                    <p className="italic text-hunter text-sm">Not yet rated</p>
                  )}
                </div>
              </Link>

              <p className="text-sm"><strong>Created by:</strong> {location.username}</p>
              <p className="text-sm"><strong>Type:</strong> {location.locationType}</p>
              <p className="text-sm"><strong>Address:</strong> {location.address}</p>
              <p className="text-sm">{location.description}</p>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-center text-lg">No locations found.</p>
      )}
    </div>
  );
}
