import React, { useState, useEffect } from 'react';
import axios from 'axios';
import backendURL from '../apiConfig';
import { Link } from 'react-router-dom';

export default function ProfilePage() {
  const [user, setUser] = useState(null);
  const [locations, setLocations] = useState([]);
  const [reviews, setReviews] = useState([]);

  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      const parsedUser = JSON.parse(storedUser);
      setUser(parsedUser);

      // Fetch user's locations
      axios.get(`${backendURL}/api/locations`)
        .then(res => {
          const userLocations = res.data.filter(location => location.username === parsedUser.username);
          setLocations(userLocations);
        })
        .catch(err => console.error('Error fetching locations:', err));

      // Fetch user's reviews
      axios.get(`${backendURL}/api/reviews`)
        .then(res => {
          const userReviews = res.data.filter(review => review.username === parsedUser.username);
          setReviews(userReviews);
        })
        .catch(err => console.error('Error fetching reviews:', err));
    }
  }, []);

  const renderStars = (rating) => {
    const stars = [];
    const roundedRating = Math.round(rating);
    for (let i = 0; i < 5; i++) {
      if (i < roundedRating) {
        stars.push(<span key={i} className="text-hunter text-xl">★</span>);
      } else {
        stars.push(<span key={i} className="text-hunter text-xl">☆</span>);
      }
    }
    return stars;
  };

  return (
    <div className="min-h-screen px-6 py-12 bg-inherit text-brunswick font-inknut">
      <h1 className="text-4xl font-bold text-center mb-10">Profile</h1>

      {/* Locations Section */}
      <h2 className="text-2xl font-semibold mb-6">My Locations</h2>
      <div className="overflow-x-auto flex space-x-6 pb-4">
        {locations.length > 0 ? (
          locations.map((location) => (
            <div key={location._id} className="min-w-[300px] bg-asparagus rounded-lg shadow-md p-4 flex-shrink-0">
              <Link to={`/locations/${location._id}`}>
                <h3 className="text-xl font-bold mb-2">{location.locationName}</h3>
              </Link>
              {/* Average stars (basic version for now) */}
              <p className="italic text-sm text-brunswick mb-2">Not yet rated</p>
              <p className="text-sm"><strong>Type:</strong> {location.locationType}</p>
              <p className="text-sm"><strong>Address:</strong> {location.address}</p>
              <p className="text-sm mt-2">{location.description}</p>
            </div>
          ))
        ) : (
          <p className="text-gray-500 italic">You haven't posted any locations yet.</p>
        )}
      </div>

      {/* Reviews Section */}
      <h2 className="text-2xl font-semibold mt-12 mb-6">My Reviews</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {reviews.length > 0 ? (
          reviews.map((review) => (
            <div key={review._id} className="bg-asparagus rounded-lg shadow-md p-4">
              <h3 className="text-lg font-bold mb-2">{review.locationName}</h3>
              <div className="flex mb-2">
                {renderStars(review.rating)}
              </div>
              <p className="text-sm">{review.description}</p>
            </div>
          ))
        ) : (
          <p className="text-gray-500 italic">You haven't written any reviews yet.</p>
        )}
      </div>
    </div>
  );
}
