import React, { useState } from 'react';
import axios from 'axios'; // Make sure Axios is imported

const CreateLocationPage = () => {
  const [locationName, setLocationName] = useState('');
  const [locationType, setLocationType] = useState('');
  const [address, setAddress] = useState('');
  const [error, setError] = useState(null);

  // Assume the logged-in user's username is available in localStorage
  const user = JSON.parse(localStorage.getItem('user'));

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Only submit if there's a valid user and all required fields are filled
    if (!user) {
      setError('You must be logged in to create a location');
      return;
    }

    const newLocation = {
      locationName,
      username: user.username, // Automatically fill the username from logged-in user
      locationType,
      address,
    };

    try {
      // Axios POST request to create the location
      const response = await axios.post('http://localhost:5000/api/locations/createLocation', newLocation);
      console.log('Location created:', response.data);
      // Redirect or show success message here
    } catch (error) {
      console.error('Error creating location:', error);
      setError('Failed to create location, please try again later.');
    }
  };

  return (
    <div className="container">
      <h1>Create a New Location</h1>
      {error && <p className="error">{error}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Location Name</label>
          <input
            type="text"
            value={locationName}
            onChange={(e) => setLocationName(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Location Type</label>
          <input
            type="text"
            value={locationType}
            onChange={(e) => setLocationType(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Address</label>
          <input
            type="text"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
            required
          />
        </div>
        <button type="submit">Create Location</button>
      </form>
    </div>
  );
};

export default CreateLocationPage;
