import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

export default function CreateLocationPage() {
    const [locationName, setLocationName] = useState('');
    const [username, setUsername] = useState('');
    const [locationType, setLocationType] = useState('');
    const [address, setAddress] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();  // This will allow navigation after creation

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Validation
        if (!locationName || !username || !locationType || !address) {
            setError('All fields are required');
            return;
        }

        try {
            const response = await fetch('http://localhost:8081/api/locations/createLocation', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ locationName, username, locationType, address }),
            });

            const data = await response.json();

            if (response.ok) {
                // Redirect to another page (e.g., locations list) after successful creation
                navigate('/locations');
            } else {
                setError(data.error || 'Failed to create location');
            }
        } catch (error) {
            console.error('Error creating location:', error);
            setError('An error occurred. Please try again.');
        }
    };

    return (
        <div className="flex flex-col items-center justify-center h-screen bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-white">
            <h1 className="text-4xl font-bold mb-6">Create a New Location</h1>

            {error && (
                <div className="text-red-500 mb-4">
                    <p>{error}</p>
                </div>
            )}

            <form onSubmit={handleSubmit} className="w-full max-w-md">
                <div className="mb-4">
                    <input
                        type="text"
                        value={locationName}
                        onChange={(e) => setLocationName(e.target.value)}
                        placeholder="Location Name"
                        className="w-full px-4 py-2 border rounded-lg"
                    />
                </div>
                <div className="mb-4">
                    <input
                        type="text"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        placeholder="Username"
                        className="w-full px-4 py-2 border rounded-lg"
                    />
                </div>
                <div className="mb-4">
                    <input
                        type="text"
                        value={locationType}
                        onChange={(e) => setLocationType(e.target.value)}
                        placeholder="Location Type"
                        className="w-full px-4 py-2 border rounded-lg"
                    />
                </div>
                <div className="mb-6">
                    <input
                        type="text"
                        value={address}
                        onChange={(e) => setAddress(e.target.value)}
                        placeholder="Address"
                        className="w-full px-4 py-2 border rounded-lg"
                    />
                </div>
                <button
                    type="submit"
                    className="w-full py-3 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition"
                >
                    Create Location
                </button>
            </form>
        </div>
    );
}
