import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import backendURL from '../apiConfig';

export default function ProfilePage() {
  const [user, setUser] = useState(null);
  const [userLocations, setUserLocations] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem('user'));
    if (!storedUser) {
      navigate('/login');
      return;
    }
    setUser(storedUser);

    axios.get(`${backendURL}/api/locations`)
      .then((res) => {
        const allLocations = res.data;
        const filtered = allLocations.filter(loc => loc.username === storedUser.username);
        setUserLocations(filtered);
      })
      .catch((error) => {
        console.error('Error fetching locations:', error);
      });
  }, [navigate]);

  return (
    <div className="min-h-screen pt-20 px-6 bg-inherit text-brunswick font-inknut">
      <h1 className="text-4xl font-bold mb-6 text-center">Your Locations</h1>

      {userLocations.length > 0 ? (
        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 max-w-4xl mx-auto">
          {userLocations.map(location => (
            <div key={location._id} className="p-6 bg-alabaster rounded-lg shadow-lg">
              <Link to={`/locations/${location._id}`}>
                <h2 className="text-2xl font-bold mb-2 text-fern hover:text-hunter">{location.locationName}</h2>
              </Link>
              <p className="mb-1"><strong>Type:</strong> {location.locationType}</p>
              <p className="mb-1"><strong>Address:</strong> {location.address}</p>
              <p>{location.description}</p>
            </div>
          ))}
        </div>
      ) : (
        <p className="text-center text-lg">You haven't submitted any locations yet.</p>
      )}
    </div>
  );
}
