import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import backendURL from '../apiConfig';

export default function AllLocationsPage() {
  const [locations, setLocations] = useState([]);
  const [sortType, setSortType] = useState('newest'); // default to newest
  const [error, setError] = useState('');

  useEffect(() => {
    axios.get(`${backendURL}/api/locations`)
      .then((res) => {
        setLocations(res.data);
      })
      .catch((err) => {
        console.error('Error fetching locations:', err);
        setError('Failed to load locations.');
      });
  }, []);

  const renderStars = (rating) => {
    const stars = [];
    const rounded = Math.round(rating);
    for (let i = 0; i < 5; i++) {
      if (i < rounded) {
        stars.push(<span key={i} className="text-hunter text-xl">★</span>);
      } else {
        stars.push(<span key={i} className="text-hunter text-xl">☆</span>);
      }
    }
    return stars;
  };

  const sortedLocations = [...locations].sort((a, b) => {
    if (sortType === 'alphabetical') {
      return a.locationName.localeCompare(b.locationName);
    } else if (sortType === 'newest') {
      return new Date(b.createdAt) - new Date(a.createdAt);
    } else {
      return 0;
    }
  });

  return (
    <div className="min-h-screen pt-20 px-4 flex flex-col items-center bg-inherit font-inknut text-brunswick">
      <h1 className="text-4xl font-bold mb-8">All Locations</h1>

      {/* Filter Buttons */}
      <div className="flex space-x-4 mb-8">
        <button
          onClick={() => setSortType('newest')}
          className={`px-4 py-2 rounded font-semibold transition ${sortType === 'newest' ? 'bg-fern text-alabaster' : 'bg-alabaster text-brunswick hover:bg-fern hover:text-alabaster'}`}
        >
          Newest
        </button>
        <button
          onClick={() => setSortType('alphabetical')}
          className={`px-4 py-2 rounded font-semibold transition ${sortType === 'alphabetical' ? 'bg-fern text-alabaster' : 'bg-alabaster text-brunswick hover:bg-fern hover:text-alabaster'}`}
        >
          A-Z
        </button>
      </div>

      {error && <p className="text-imperial">{error}</p>}

      {sortedLocations.length > 0 ? (
        <div className="w-full max-w-6xl flex overflow-x-auto space-x-6 py-4">
          {sortedLocations.map((location) => (
            <div key={location._id} className="flex-shrink-0 w-80 bg-alabaster rounded-lg shadow-md p-6 transform hover:scale-105 transition duration-300 ease-in-out">
              <Link to={`/locations/${location._id}`}>
                <h2 className="text-2xl font-bold text-fern hover:text-hunter mb-2 text-center">
                  {location.locationName}
                </h2>

                {/* You could add average rating here later if you want */}
                <div className="flex justify-center items-center mb-4">
                  {location.averageRating !== undefined && location.averageRating !== null ? (
                    renderStars(location.averageRating)
                  ) : (
                    <p className="italic text-hunter text-sm">Not yet rated</p>
                  )}
                </div>
              </Link>

              <p className="text-sm text-center mb-1"><strong>Type:</strong> {location.locationType}</p>
              <p className="text-sm text-center mb-1"><strong>Address:</strong> {location.address}</p>
              <p className="text-sm text-center">{location.description}</p>
            </div>
          ))}
        </div>
      ) : (
        <p>No locations found.</p>
      )}
    </div>
  );
}
