import React from 'react';
import { useNavigate } from 'react-router-dom';

export default function LandingPage() {
    const navigate = useNavigate();

    // Retrieve user data from localStorage
    const userData = JSON.parse(localStorage.getItem('userData'));

    // Check if user data exists
    if (!userData) {
        return (
            <div className="flex flex-col items-center justify-center h-screen bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-white">
                <p>You need to log in first!</p>
            </div>
        );
    }

    // Function to handle logout
    const handleLogout = () => {
        localStorage.removeItem('userData'); // Clear user data
        navigate('/login'); // Redirect to the login page
    };

    return (
        <div className="flex flex-col items-center justify-center h-screen bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-white">
            <h1 className="text-4xl font-bold mb-6">
                Welcome to Demeter, {userData.username}
            </h1>
            <p className="text-xl mb-6">Email: {userData.email}</p>

            {/* Log Out Button */}
            <button
                onClick={handleLogout}
                className="px-6 py-3 bg-red-500 text-white rounded-lg hover:bg-red-600 transition"
            >
                Log Out
            </button>
        </div>
    );
}
