import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate

export default function LandingPage() {
    const [user, setUser] = useState(null);
    const navigate = useNavigate(); // Initialize useNavigate

    useEffect(() => {
        const storedUser = localStorage.getItem('user');

        // Check if storedUser exists and is a valid JSON string
        if (storedUser && storedUser !== 'undefined') {
            try {
                const parsedUser = JSON.parse(storedUser);
                setUser(parsedUser); // Store parsed user data in state
            } catch (error) {
                console.error("Error parsing user data from localStorage", error);
            }
        }
    }, []);

    const handleLogOut = () => {
        localStorage.removeItem('user'); // Remove user data from localStorage
        navigate('/login'); // Redirect to login page
    };

    useEffect(() => {
      try {
          const storedUser = localStorage.getItem('user');
          if (storedUser) {
              const userData = JSON.parse(storedUser); // Parse the data if it exists
              setUser(userData); // Store user data in state
          } else {
              setError('No user data found.');
          }
      } catch (err) {
          setError('Error parsing user data.');
      }
  }, []);


    return (
        <div className="flex flex-col items-center justify-center h-screen bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-white">
            <h1 className="text-4xl font-bold mb-6">
                Welcome to Demeter{user ? `, ${user.username}` : ''}
            </h1>
            {user ? (
                <div>
                    <p>Email: {user.email}</p>
                </div>
            ) : (
                <p>Loading user data...</p> // Show loading message if user is not found
            )}
            <button
                onClick={handleLogOut} // Log out handler
                className="px-6 py-3 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition mt-4"
            >
                Log Out
            </button>
        </div>
    );
}
