import React, { useState } from "react";
import axios from "axios";

const CreateLocationPage = () => {
  const [locationData, setLocationData] = useState({
    locationName: "",
    locationType: "",
    address: "",
  });

  const handleChange = (e) => {
    setLocationData({ ...locationData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const storedUser = localStorage.getItem("user");
    const user = storedUser ? JSON.parse(storedUser) : null;

    if (!user) {
      console.error("No user data found");
      return;
    }

    try {
      const response = await axios.post("http://localhost:5000/api/locations/createLocation", {
        ...locationData,
        username: user.username,  // Adding the user’s username to the data
      });
      console.log("Location created successfully", response.data);
    } catch (error) {
      console.error("Error creating location: ", error);
    }
  };

  return (
    <div className="container">
      <h1>Create a new location</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Location Name</label>
          <input
            type="text"
            name="locationName"
            value={locationData.locationName}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Location Type</label>
          <input
            type="text"
            name="locationType"
            value={locationData.locationType}
            onChange={handleChange}
            required
          />
        </div>
        <div>
          <label>Address</label>
          <input
            type="text"
            name="address"
            value={locationData.address}
            onChange={handleChange}
            required
          />
        </div>
        <button type="submit">Create Location</button>
      </form>
    </div>
  );
};

export default CreateLocationPage;
